// character.h 11/6/92#ifndef CHARACTER_H#define CHARACTER_H#include "comparablething.h"#include "error.h"class character: public comparablething{protected:	char data;public:	character( char c ): comparablething( "character" )	{		data = c;	}	character( const character& c ): comparablething( c )	{		data = c.data;	}	~character()	{	}	void print( ostream& os ) const	{		os << data;	}	boolean operator==( const equatablething& t ) const	{		return t.isa( myclass ) && data == ((const character &)t).data;	}	boolean operator>( const comparablething& t) const	{		boolean equal;		if (t.isa( myclass ))			equal = data > ((const character &)t).data;		else			error( "character::operator= cannot compare to non character" );		return equal;	}	thing* copy() const	{		return new character(*this);	}};ostream& operator<<( ostream& os, const character& c );#endif